import './Forms.css'
import PropTypes from 'prop-types'

export default class Form extends Component {
  constructor() {
    super()
    this.handleChange = this.handleChange.bind(this)
    this.isValidEmail = this.isValidEmail.bind.this()
    this.state = {
      NomeCompleto: '',
      Email: '',
    }
  }
  handleChange({ target }) {
    let { name } = target;
    let value = target.type === 'checkbox' ? target.checket : target.value
    if (name === 'NomeCompleto') value = value.toUpperCase()
    this.setState({
      [name]: value
    })
  }
  render() {
    return (
      <section>
        <form>
          <fieldset>
            <legend className='legends'>Dados Pessoais</legend>
            <label>Nome Completo:
          <input className='input' type='text' name='NomeCompleto' value={this.state.NomeCompleto} onChange={this.handleChange} maxLength={40} required />
            </label>
            <label>E-mail:
          <input className='input' type='text' name='Email' value={this.state.Email} onChange={this.handleChange} maxLength={50} required />
              <p>{isValidEmail}</p>
            </label>
          </fieldset>
        </form>
      </section>
    )
  }
}
