import React, { Component } from 'react'
import './Forms.css'
import PropTypes from 'prop-types'

export default class Form extends Component {
  constructor() {
    super()
    this.toUpperCase = this.toUpperCase.bind(this)
    this.handleChange = this.handleChange.bind(this)
    this.state = {
      NomeCompleto: '',
    }
  }
  handleChange({ target }) {
    const { name } = target;
    const value = target.type === 'checkbox' ? target.checket : target.value
    this.setState({
      [name]: value
    })
  }
  toUpperCase(par) {
    this.toUpperCase(par)
  }
  render() {
    return (
      <section>
        <form>
          <fieldset>
            <label>Nome Completo:
          <input className='input' type='text' name='NomeCompleto' value={this.state.NomeCompleto} onChange={toUpperCase(this.handleChange)} maxLength={40} required />
            </label>
          </fieldset>
        </form>
      </section>
    )
  }
}
