import React, { Component } from 'react'
import './Forms.css'
import PropTypes from 'prop-types'

export default class Form extends Component {
  constructor() {
    super()
    this.handleChange = this.handleChange.bind(this)
    this.isValidEmail = this.isValidEmail.bind(this)
    this.isValidAddress = this.isValidAddress.bind(this)
    this.state = {
      NomeCompleto: '',
      Email: '',
      Endere√ßo: '',
    }
  }
  handleChange({ target }) {
    let { name } = target;
    let value = target.type === 'checkbox' ? target.checket : target.value
    if (name === 'NomeCompleto') value = value.toUpperCase()
    this.setState({
      [name]: value
    })
  }

  isValidEmail() {
    let result = '';
    if (this.state.Email !== '') {
      this.state.Email.match(/^([\w.%+-]+)@([\w-]+\.)+([\w]{3})$/i) ? result = <span style={{ color: 'green' }}> ‚úÖ This is a valid e-mail üòÉ </span> : result = <span style={{ color: 'red' }}> ‚ùå This isn't a valid e-mail üòü </span>
    }
    return result
  }

  isValidAddress() {
    let result = '';
    if (this.state.Endere√ßo !== '' && this.state.Endere√ßo.match(/[_=+¬ß*?!#$%&¬®¬¨@\\(\)]/)) result = <span style={{ color: 'red' }}> ‚ùå This isn't a valid address üòü</span>
    return result
  }
  render() {
    return (
      <section>
        <form>
          <fieldset className='fieldset'>
            <legend className='legends'>Dados Pessoais</legend>
            <label>Nome Completo:
          <input className='input' type='text' name='NomeCompleto' value={this.state.NomeCompleto} onChange={this.handleChange} maxLength={40} required />
            </label> <br />
            <label>E-mail:
          <input className='input' type='email' name='Email' value={this.state.Email} onChange={this.handleChange} maxLength={50} required />
            </label>
            <>{this.isValidEmail()}</>
            <label>Endere√ßo:
          <input className='input' type='text' name='Endere√ßo' value={this.state.Endere√ßo} onChange={this.handleChange} maxLength={200} required />
            </label>
            <>{this.isValidAddress()}</>
          </fieldset>
        </form>
      </section>
    )
  }
}
