import React, { Component } from 'react'
import './Forms.css'
import PropTypes from 'prop-types'

export default class Form extends Component {
  constructor() {
    super()
    this.handleChange = this.handleChange.bind(this)
    this.updateState = this.updateState.bind(this)
    this.state = {
      NomeCompleto: '',
    }
  }
  handleChange({ target }) {
    let { name, value } = target;
    // const value = target.type === 'checkbox' ? target.checket : target.value
    if (name === 'NomeCompleto') value.toUpperCase()
    this.updateState(name, value)
  }

  updateState(name, value) {
    this.setState({
      [name]: value,
    })
  }
  render() {
    return (
      <section>
        <form>
          <fieldset>
            <label>Nome Completo:
          <input className='input' type='text' name='NomeCompleto' value={this.state.NomeCompleto} onChange={this.handleChange} maxLength={40} required />
            </label>
          </fieldset>
        </form>
      </section>
    )
  }
}
